import { TOGGLE_SELECTED_STATE } from './actions';


const initialState = {
    products: {
        1: {
            name: 'яблоко',
            selected: true
        },
        2: {
            name: 'орехи',
            selected: false
        },
        3: {
            name: 'печенье',
            selected: false
        },
    }
}

export const productsReducer = (state = initialState, action) => {
    switch (action.type) {
        case TOGGLE_SELECTED_STATE: {
            const newState = state
            newState.products[action.payload].selected = !newState.products[action.payload].selected
            return newState
        }
        default: {
            return state
        }
    }
}
