import { recipesConnect } from '../../connect/recipes/recipes'

export const widget = ({recipeId, recipes}) => {
    recipeId = +recipeId
    let recipeMain = {};
    for (let recipe of recipes) {
        if (recipe.id === recipeId) {
            recipeMain = {...recipe};
            break;
        }
    }
    console.log(recipeMain);
    return (
        <div className='container'>
            <div style={{
                marginLeft: '25%',
                display: 'flex',
                flexDirection: 'column',
                minHeight: '80vh',
                width: '40%',
            }}>
                <h4 style={{display: 'inline-block',borderBottom: '2px solid black'}}>{recipeMain.title}</h4>
            </div>
        </div>
    )
}

export const RecipeInfo = recipesConnect(widget)
