import { productsConnect } from '../../../connect/products/products'
import CheckIcon from '@mui/icons-material/Check';
import './style.css';
import Paper from '@mui/material/Paper';
import InputBase from "@mui/material/InputBase";
import {styled} from "@mui/material/styles";
import ButtonUnstyled, {buttonUnstyledClasses} from "@mui/core/ButtonUnstyled";


const CustomButtonRoot = styled('button')`
  background-color: #ffffff;
  padding: 15px 20px;
  border-radius: 15px 4px 4px 15px;
  color: #000000;
  font-weight: 600;
  font-family: Helvetica, Arial, sans-serif;
  font-size: 14px;
  transition: all 200ms ease;
  cursor: pointer;
  box-shadow: 0 4px 20px 0 rgba(61, 71, 82, 0.1), 0 0 0 0 rgba(0, 127, 255, 0);
  border: none;
  border: 2px solid #000000;

  &:hover {
    background-color: rgb(81, 17, 126);
  }

  &.${buttonUnstyledClasses.active} {
    background-color: rgb(27, 7, 73);
  }

  &.${buttonUnstyledClasses.focusVisible} {
    box-shadow: 0 4px 20px 0 rgba(61, 71, 82, 0.1), 0 0 0 5px rgba(0, 127, 255, 0.5);
    outline: none;
  }
`;

function CustomButton(props) {
    return <ButtonUnstyled {...props} component={CustomButtonRoot} />;
}


const widget = ({products, changeSelectedState}) => {
    const checkProduct = (item) => {
        changeSelectedState(item)
    }

    return (
        <div>
            {<Paper elevation={3} style={{width: '30%', borderRadius: '7px', padding: '10px'}}>
                <div style={{display: 'flex', justifyContent: 'space-between', marginBottom: '25px'}}>
                    <InputBase
                        sx={{m: 0}} placeholder="Type chat name here..."
                    />
                    <CustomButton>Search</CustomButton>
                </div>
                {Object.keys(products).map(elKey => (
                    <div key={elKey} className="block" onClick={() => checkProduct(elKey)}>
                        <p>{products[elKey].name}</p>
                        <CheckIcon hidden={!products[elKey].selected}/>
                    </div>))
                }
            </Paper>}
        </div>
    )
}

export const ListOfProducts = productsConnect(widget)
