import { Footer } from "../../components/Footer/footer";
import { useState, useEffect } from 'react';

let collectProducts = [];
const Resipes = () => {
    async function getRecipes() {
        const url = 'https://raw.githubusercontent.com/rumblfn/Cookery/main/jsonSettings/recipes.json';
        try {
            const response = await fetch(url);
            const data = await response.json();
            collectProducts = [];
            let i = 0
            for (let elem of data) {
                collectProducts.push(elem);
                collectProducts[i].currentImage = 0
                i++;
            }
        } catch (e) {
            console.error(e);
        }
    }
    console.log(collectProducts);
    useEffect(() => {
        getRecipes();
    }, []);

    return (
        <div className="container">
            <div className="products_container">
                {collectProducts.map((item) => (
                    <div className="product_box"
                        key={item.id}
                    >
                        <img src={item.images[item.currentImage]} />
                        <h3>{item.title}</h3>
                    </div>
                ))}
            </div>

        </div >
    )
}
export default Resipes;
