import { Footer } from "../../components/Footer/footer";
import { useState, useEffect } from 'react';
// import './style.css';

let collectProducts = [];
const Resipes = () => {
    async function getRecipes() {
        const url = 'https://raw.githubusercontent.com/rumblfn/Cookery/main/jsonSettings/recipes.json';
        try {
            const response = await fetch(url);
            const data = await response.json();
            collectProducts = [];
            let i = 0
            for (let elem of data) {
                collectProducts.push(elem);
                collectProducts[i].currentImage = 0
                i++;
            }
        } catch (e) {
            console.error(e);
        }
    }

    const changeImageIncrease = (item) => {
        console.log(item);
        item.currentImage++;
        if (item.currentImage > item.images.length - 1) {
            item.currentImage = 0;
        }
    }

    const changeImagedecrease = (item) => {
        console.log(item);
        item.currentImage--;
        if (item.currentImage < 0) {
            item.currentImage = item.images.length - 1;
        }
    }

    useEffect(() => {
        getRecipes();
    }, []);

    return (
        <div className="container">
            <div className="products_container">
                {collectProducts.map((item) => (
                    <div className="product_box"
                        key={item.id}
                    >
                        <button onClick={(e) => changeImagedecrease(item)}>
                            left
                        </button>
                            <img src={item.images[item.currentImage]} alt='food_image'/>
                        <button onClick={(e) => changeImageIncrease(item)}>
                            right
                        </button>
                        <h3>{item.title}</h3>
                    </div>
                ))}
            </div>

        </div >
    )
}
export default Resipes;
