import { useState } from "react";
import { auth } from "../../firebase";
import { Link } from 'react-router-dom';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import '../Login/loginAndSignUpStyles.css';
import Alert from '@mui/material/Alert';
import Button from '@mui/material/Button';

export const Registration = () => {
    const [name, setName] = useState("")
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [nameError, setNameError] = useState("");
    const [passwordError, setPasswordError] = useState("");
    const mediumRegex = new RegExp("^(((?=.*[A-Z])(?=.*[0-9])(?=.*[a-z])))(?=.{8,})");

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log(name, email, password)
        if (name.length < 5) {
            setNameError("Длина имени короче 5");
        }
        if (!mediumRegex.test(password)) {
            setPasswordError("Некорректно введён пароль")
        }

        if (!passwordError && !nameError) {
            console.log(name, email, password)
        }

        // try {
        //     await auth.createUserWithEmailAndPassword(email, password);

        //     setEmail('')
        //     setPassword('')

        // } catch (error) {
        //     setError(error.message);
        // }
    };

    return (
        <div className='mainPage'>
            <form onSubmit={handleSubmit}>
                <h4>Регистрация</h4>
                <Box
                    sx={{
                        display: 'flex',
                        flexDirection: 'column',
                        width: '100%'
                    }}
                >
                    <TextField
                        sx={{m: 1, mt: 3}}
                        id="demo-helper-text-misaligned-no-helper"
                        label="Отображаемое имя"
                        name="name"
                        color="background"
                        onChange={(e) => {if (name.length > 4) {setNameError('')} setName(e.target.value)}}
                        value={name}
                        type="name"
                    />
                    <TextField
                        id="demo-helper-text-misaligned-no-helper"
                        label="Email"
                        name="email"
                        type="email"
                        color="background"
                        onChange={(e) => {setEmail(e.target.value)}}
                        value={email}
                        sx={{m: 1}}
                    />
                    <TextField
                        sx={{m: 1, mb: 3}}
                        id="demo-helper-text-misaligned-no-helper"
                        label="Password"
                        name="password"
                        color="background"
                        onChange={(e) => {if (mediumRegex.test(password)) {setPasswordError('')} setPassword(e.target.value)}}
                        value={password}
                        type="password"
                    />
                </Box>
                <div>
                    {nameError && <Alert sx={{mb: 2}} severity="error">{nameError}</Alert>}
                    {passwordError && <Alert sx={{mb: 2}} severity="error">{passwordError}</Alert>}
                    <Button 
                        style={{
                            borderColor: "#000000",
                            color: '#000000',
                        }}
                        variant="outlined" 
                        type="submit"
                    >Создать аккаунт
                    </Button>
                </div>
                <br/>
                <p>
                    Уже есть аккаунт? <Link to="/login">Войдите</Link>
                </p>
            </form>
        </div>
    );
}
