import { SET_USER_DATA, STAR_RECIPE } from './actions';

const initialState = {
    loged: false,
    id: null,
    name: null,
    mail: null,
    likes: null,
    recipes: [],
    likedPostsIdes: [],
}

export const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_USER_DATA: {
            return {...state, ...action.payload, 
                loged: true, likedPostsIdes: JSON.parse(action.payload.likedPostsIdes) || []}
        }
        case STAR_RECIPE: {
            switch (action.payload.starred) {
                case true: {
                    console.log(action.payload)
                    let likedPostIdesCopy = [...state.likedPostsIdes]
                    const index = likedPostIdesCopy.indexOf(action.recipeId);
                    if (index > -1) {
                        likedPostIdesCopy.splice(index, 1);
                    }
                    return {...state, likedPostsIdes: likedPostIdesCopy}
                }
                case false: {
                    return {...state, likedPostsIdes: [...state.likedPostsIdes, action.payload.recipeId]}
                }
                default: {
                    return state
                }
            }
        }
        default: {
            return state
        }
    }
}
