import CloseRoundedIcon from '@mui/icons-material/CloseRounded';
import { Link } from 'react-router-dom';
import InputBase from "@mui/material/InputBase";
import { ListOfProducts } from '../HomePage/listOfProducts/listOfProducts'
import Button from "@mui/material/Button";
import ArrowBackRoundedIcon from '@mui/icons-material/ArrowBackRounded';
import ArrowForwardRoundedIcon from '@mui/icons-material/ArrowForwardRounded';
import { ListOfSelectedProducts } from '../../components/HomePage/listOfSelectedProducts/listOfSelectedProducts';
import { selectedProductsConnect } from '../../connect/selectedProducts/selectedProducts'
import { Routes, Route, useNavigate } from 'react-router-dom';
import { useState, useRef } from 'react';
import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown';
import ArrowDropUpIcon from '@mui/icons-material/ArrowDropUp';
import Paper from '@mui/material/Paper';

export const WidgetSelectedProducts = ({title, time, actions, setActions}) => {
    let newAction = '';
    let [iconImage, setIconImage] = useState(true)
    const textInput = useRef();

    const cleanAndFocus = () => {
        textInput.current.value = '';
        textInput.current.focus();
    }

    return (
        <div style={{padding: '16px'}}>
            <Link to='/profile/create'>
                <Button 
                    style={{
                        marginBottom: '16px',
                        borderColor: "#000000",
                        color: '#000000',
                    }}
                    variant="outlined" 
                    type="submit"
                >
                    <ArrowBackRoundedIcon/> Вернуться
                </Button>
            </Link>
            <div style={{display: 'flex'}}>
                <h4 style={{marginRight: '2%'}}>{title}</h4>
                <h4 style={{marginRight: '2%'}}>{time}</h4>
            </div>
            <Button 
                style={{
                    color: '#000000',
                    border: 'none',
                    marginBottom: '2%'
                }}
                variant="outlined" 
                type="submit"
                onClick={() => {
                    if (iconImage) {
                        setIconImage(false)
                    } else {
                        setIconImage(true)
                    }
                }}
                >Выбранные продукты {iconImage ? <ArrowDropDownIcon/> : <ArrowDropUpIcon/>}
            </Button>
            <ListOfSelectedProducts 
                maxHeightProp='60vh'
                show={iconImage}
            />
            <h6 style={{marginTop: '8px'}}>Способ приготовления</h6>
            <div style={{display: 'flex'}}>
                <InputBase
                    ref={textInput}
                    sx={{width: '100%'}} 
                    style={{borderBottom: '2px solid black'}}
                    placeholder="Введите название"
                    onChange={(e) => {
                        newAction = e.target.value;
                    }}
                />
                <Button 
                    style={{
                        marginLeft: '16px',
                        borderColor: "#000000",
                        color: '#000000',
                    }}
                    variant="outlined" 
                    type="submit"
                    onClick={() => {
                        if (newAction) {
                            cleanAndFocus()
                            setActions([
                                ...actions,
                                newAction
                            ]);
                        }
                    }}
                    >Добавить
                </Button>
            </div>
            <div style={{display: 'flex', flexDirection: 'column'}}>
                {actions.map((item, index) => (
                    <p key={index} style={{marginTop: 'auto', marginBottom: 'auto'}}>
                        <span>{index + 1}</span>. {item}
                    </p>))
                }
            </div>
        </div>
    )
}


export const Widget = ({title, setTitle, time ,setTime, products}) => {
    let navigate = useNavigate();

    const checkForm = () => {
        if (title && time && Object.keys(products).length) {
            navigate("/profile/create/checkProducts");
        }
    }

    return (
        <div>
            <InputBase
                    sx={{m: 2, width: '90%'}} 
                    style={{borderBottom: '2px solid black'}}
                    placeholder="Введите название"
                    onChange={(e) => {
                        setTitle(e.target.value);
                    }}
                />
                <div style={{
                    display: 'flex',
                }}>
                    <p style={{margin: '16px'}}>Введите время приготовления</p>
                    <input 
                        style={{margin: '10px 0'}} 
                        type="time" id="appt" 
                        name="appt" required
                        onChange={(e) => {setTime(e.target.value)}}/>
                </div>
                <div style={{
                    margin: '16px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'space-between',
                }}>
                    <p style={{
                            marginBottom: '0px',
                    }}>Выберите подходящие продукты</p>
                    <Button 
                        style={{
                            marginLeft: '16px',
                            borderColor: "#000000",
                            color: '#000000',
                        }}
                        variant="outlined" 
                        type="submit"
                        onClick={checkForm}>
                        Дальше <ArrowForwardRoundedIcon/>
                    </Button>
                </div>
                <ListOfProducts maxHeightProp='60vh'/>
        </div>
    )
}


export const WidgetMain = selectedProductsConnect(Widget)


export const CreateRecipeBlock = () => {
    const [title, setTitle] = useState('')
    const [time, setTime] = useState('')
    const [actions, setActions] = useState([])
    // const [products, setProducts] = useState({})

    return (
        <div style={{ 
            border: '2px solid black',
            width: '100vw',
            height: '100vh',
            position: 'fixed',
            top: '0px',
            left: '0px',
            display: 'flex',
            justifyContent: 'center',
            background: 'rgba(0,0,0,.5)',
            overflow: 'auto'
        }}>
            <div style={{
                backgroundColor: '#FFFFFF',
                position: 'absolute',
                top: '10%',
                width: '50%',
                minHeight: '60%',
                borderRadius: '20px',
            }}>
                <div style={{
                    position: 'absolute',
                    top: '16px',
                    right: '16px',
                }}>
                    <Link to='/profile'>
                        <CloseRoundedIcon style={{
                            color: '#000000',
                        }}/>
                    </Link>
                </div>
                <Routes>
                    <Route path="checkProducts/*" element={
                        <WidgetSelectedProducts 
                            title={title}
                            time={time}
                            actions={actions}
                            setActions={setActions}
                        />
                    } />
                    <Route path="/*" element={
                        <WidgetMain 
                            title={title}
                            setTitle={setTitle}
                            time={time}
                            setTime={setTime}
                        />
                    } />
                </Routes>
            </div>
        </div>
    )
}
