import CloseRoundedIcon from '@mui/icons-material/CloseRounded';
import { Link } from 'react-router-dom';
import InputBase from "@mui/material/InputBase";
import { ListOfProducts } from '../HomePage/listOfProducts/listOfProducts'
import Button from "@mui/material/Button";
import ArrowBackRoundedIcon from '@mui/icons-material/ArrowBackRounded';
import ArrowForwardRoundedIcon from '@mui/icons-material/ArrowForwardRounded';
import { ListOfSelectedProducts } from '../../components/HomePage/listOfSelectedProducts/listOfSelectedProducts';
import { Routes, Route, useNavigate } from 'react-router-dom';

export const WidgetSelectedProducts = () => {
    return (
        <div style={{padding: '16px'}}>
            <Link to='/profile/create'>
                <Button 
                    style={{
                        marginBottom: '16px',
                        borderColor: "#000000",
                        color: '#000000',
                    }}
                    variant="outlined" 
                    type="submit"
                >
                    <ArrowBackRoundedIcon/> Вернуться
                </Button>
            </Link>
            <h6 style={{marginTop: '8px'}}>Проверьте правильность выбранных продуктов</h6>
            <ListOfSelectedProducts maxHeightProp='60vh'/>
            <h6 style={{marginTop: '8px'}}>Способ приготовления</h6>
        </div>
    )
}


export const WidgetMain = () => {
    let navigate = useNavigate();

    const checkForm = () => {
        navigate("/profile/create/checkProducts");
    }

    return (
        <div>
            <InputBase
                    sx={{m: 2, width: '90%'}} style={{borderBottom: '2px solid black'}} placeholder="Введите название"
                />
                <div style={{
                    display: 'flex',
                }}>
                    <p style={{margin: '16px'}}>Введите время приготовления</p>
                    <input style={{margin: '10px 0'}} type="time" id="appt" name="appt" required/>
                </div>
                <div style={{
                    margin: '16px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'space-between',
                }}>
                    <p style={{
                            marginBottom: '0px',
                    }}>Выберите подходящие продукты</p>
                    <Button 
                        style={{
                            marginLeft: '16px',
                            borderColor: "#000000",
                            color: '#000000',
                        }}
                        variant="outlined" 
                        type="submit"
                        onClick={checkForm}>
                        Дальше <ArrowForwardRoundedIcon/>
                    </Button>
                </div>
                <ListOfProducts maxHeightProp='60vh'/>
        </div>
    )
}


export const CreateRecipeBlock = () => {
    return (
        <div style={{ 
            border: '2px solid black',
            width: '100vw',
            height: '100vh',
            position: 'fixed',
            top: '0px',
            left: '0px',
            display: 'flex',
            justifyContent: 'center',
            background: 'rgba(0,0,0,.5)',
        }}>
            <div style={{
                backgroundColor: '#FFFFFF',
                position: 'absolute',
                top: '10%',
                width: '50%',
                minHeight: '60%',
                borderRadius: '20px',
            }}>
                <div style={{
                    position: 'absolute',
                    top: '16px',
                    right: '16px',
                }}>
                    <Link to='/profile'>
                        <CloseRoundedIcon style={{
                            color: '#000000',
                        }}/>
                    </Link>
                </div>
                <Routes>
                    <Route path="checkProducts/*" element={<WidgetSelectedProducts />} />
                    <Route path="/*" element={<WidgetMain />} />
                </Routes>
            </div>
        </div>
    )
}
