import CloseRoundedIcon from '@mui/icons-material/CloseRounded';
import { Link } from 'react-router-dom';
import InputBase from "@mui/material/InputBase";
import { ListOfProducts } from '../HomePage/listOfProducts/listOfProducts'
import Button from "@mui/material/Button";
import ArrowBackRoundedIcon from '@mui/icons-material/ArrowBackRounded';
import ArrowForwardRoundedIcon from '@mui/icons-material/ArrowForwardRounded';
import { ListOfSelectedProducts } from '../../components/HomePage/listOfSelectedProducts/listOfSelectedProducts';
import { selectedProductsConnect } from '../../connect/selectedProducts/selectedProducts'
import { recipesConnect } from "../../connect/recipes/recipes"
import { Routes, Route, useNavigate } from 'react-router-dom';
import { useState, useRef, useEffect } from 'react';
import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown';
import ArrowDropUpIcon from '@mui/icons-material/ArrowDropUp';
import TextareaAutosize from '@mui/material/TextareaAutosize';
import { nanoid } from 'nanoid'

export const widg = ({title, time, actions, products, description, setDescription, images, setImages, addNewRecipe}) => {
    return (
        <div style={{padding: '16px', display: 'flex', flexDirection: 'column'}}>
            <div style={{display: 'flex', marginBottom: '16px'}}>
                <Link to='/profile/create/checkProducts'>
                    <Button 
                        style={{
                            borderColor: "#000000",
                            color: '#000000',
                        }}
                        variant="outlined" 
                        type="submit"
                    >
                        <ArrowBackRoundedIcon/> Вернуться
                    </Button>
                </Link>
                <Link to='/profile'>
                    <Button
                        style={{
                            marginLeft: '16px',
                        }}
                        variant="outlined" 
                        type="submit"
                        onClick = {() => { 
                            addNewRecipe({
                                "title": title,
                                "userId": nanoid(),
                                "id": nanoid(),
                                "time": time,
                                "cook": actions,
                                "lstOfProducts": products,
                                "products": products,
                                "description": description,
                                "images": images,
                                "rating": 0,
                                "comments": []
                            })
                        }}
                        >Опубликовать
                    </Button>
                </Link>
            </div>
            <div style={{display: 'flex'}}>
            <div style={{
                background: 'rgba(0,0,0,.3)',
                borderRadius: '5px',
                width: '70%',
                height: '50vh',
                border: '2px solid black',
                display: 'flex',
                justifyContent: 'center', 
                flexDirection: 'column',
                alignItems: 'center',
            }}>
                <Button
                    style={{
                        marginLeft: '16px',
                        borderColor: "#000000",
                        color: '#000000',
                    }}
