import { SET_USER_DATA, STAR_RECIPE } from './actions';

const initialState = {
    loged: false,
    id: null,
    name: null,
    mail: null,
    likes: null,
    recipes: [],
    likedPostsIdes: [],
}

export const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_USER_DATA: {
            return {...state, ...action.payload, 
                loged: true, likedPostsIdes: JSON.parse(action.payload.likedPostsIdes) || []}
        }
        case STAR_RECIPE: {
            return {...state, likedPostsIdes: [...state.likedPostsIdes, action.payload]}
        }
        default: {
            return state
        }
    }
}
