import { productsConnect } from '../../../connect/products/products'
import CheckIcon from '@mui/icons-material/Check';
import './style.css';
import Paper from '@mui/material/Paper';
import InputBase from "@mui/material/InputBase";
import axios from 'axios';
import Button from '@mui/material/Button';
import styled from "styled-components";

const Overlay = styled.div`
    button:hover {
        background-color:#000000;
    }
`
const NewButton = styled.div`
    display:inline-block;
    padding:0.35em 1.2em;
    border:0.1em solid #000000;
    margin:0 0.3em 0.3em 0;
    border-radius:0.12em;
    box-sizing: border-box;
    text-decoration:none;
    font-family:'Roboto',sans-serif;
    font-weight:300;
    color:#000000;
    text-align:center;
    transition: all 0.2s;

    &:hover  {
        color:#000000;
        background-color:#FFFFFF;
    }
`;


const widget = ({products, changeSelectedState, filterState, marginRightProp, maxHeightProp}) => {
    let default_size = '70vh'
    const checkProduct = (item) => {
        changeSelectedState(item)
    }

    const filterProducts = (event) => {
        filterState(event.target.value);
    }

    return (
        <>
        <Overlay>
            <NewButton>
                Добавить
            </NewButton>
        </Overlay>
        <Paper elevation={3} style={{borderRadius: '7px', padding: '10px', marginRight: marginRightProp, overflow: 'auto', maxHeight: maxHeightProp || default_size}}>
            <div style={{display: 'flex', justifyContent: 'space-between', marginBottom: '25px'}}>
                <InputBase
                    onChange={filterProducts}
                    sx={{m: 0, width: '100%'}} placeholder="Введите название продукта"
                />
            </div>
            {Object.keys(products).map(elKey => (
                <div key={elKey} className="block" onClick={() => checkProduct(elKey)}>
                    <p>{products[elKey].name}</p>
                    <CheckIcon hidden={!products[elKey].selected}/>
                </div>))
            }
        </Paper>
        </>
    )
}

export const ListOfProducts = productsConnect(widget)
