import { productsConnect } from '../../../connect/products/products'
import CheckIcon from '@mui/icons-material/Check';
import './style.css';
import Paper from '@mui/material/Paper';


const widget = ({products}) => {
    console.log(products)

    const checkProduct = (item) => {
        item.selected = !item.selected
    }

    const renderProducts = (
        <Paper elevation={3} style={{width: '30%', borderRadius: '7px', padding: '10px'}}>
            {Object.keys(products).map(elKey => (
                <div className="block" onClick={(item) => checkProduct(item)}>
                    <p key={elKey}>{products[elKey].name}</p>
                    <CheckIcon hidden={!products[elKey].selected}/>
                </div>))
            }
        </Paper>)

    return (
        <div>
            {renderProducts}
        </div>
    )
}

export const ListOfProducts = productsConnect(widget)
