import { SET_USER_DATA } from './actions';

const initialState = {
    id: null,
    name: null,
    email: null,
    likes: null,
    recipes: []
}

export const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_USER_DATA: {
            return {...state, ...action.payload}
        }
        default: {
            return state
        }
    }
}
